changeSet.sql:
CREATE TABLE IF NOT EXISTS brand ( id BIGSERIAL PRIMARY KEY, name VARCHAR NOT NULL, description VARCHAR );
CREATE TABLE IF NOT EXISTS product ( id BIGSERIAL PRIMARY KEY, name VARCHAR NOT NULL, price NUMERIC(10, 2) NOT NULL, brand_id BIGINT NOT NULL, FOREIGN KEY (brand_id) REFERENCES brand (id) );
